import React, { useContext, useState } from "react";
import { useNavigate, NavLink, useLocation } from "react-router-dom";
import { Container, Nav, Navbar } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Navbar.css";
import { AuthContext } from "../context/AuthContext";
import LogoutModal from "./LogoutModal";
import logo from "../logo/pbsheader.png";
import userAvatar from "../logo/user.png";

const Navigation = () => {
  const { user, logout, loading } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();
  const [showLogoutModal, setShowLogoutModal] = useState(false);

  const handleLogoutClick = () => {
    setShowLogoutModal(true);
  };

  const handleLogoutConfirm = () => {
    logout();
    setShowLogoutModal(false);
    navigate("/");
  };

  const handleLogoutCancel = () => {
    setShowLogoutModal(false);
  };

  const hasRole = (roles) => {
    return user && roles.includes(user.role);
  };

  const navLinks = [
    { to: "/homepage", label: "Home", roles: ["reporter", "producer", "editor", "super", "programmer"], icon: (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-home"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2h-4a2 2 0 0 1-2-2v-4H9v4a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2z"/></svg>
    ) },
    { to: "/news", label: "News", roles: ["reporter", "producer", "editor", "super", "programmer"], icon: (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-file-text"><path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"/><polyline points="14 2 14 8 20 8"/></svg>
    ) },
    { to: "/approval", label: "Approval", roles: ["producer", "editor", "super", "programmer"], icon: (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-check-circle"><circle cx="12" cy="12" r="10"/><polyline points="9 12 12 15 16 10"/></svg>
    ) },
    { to: "/tags", label: "Tags", roles: ["producer", "editor", "super", "programmer"], icon: (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-tag"><path d="M20 12v7a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2v-7"/><path d="M14 7a2 2 0 1 0-4 0 2 2 0 0 0 4 0z"/></svg>
    ) },
    { to: "/approvedreports", label: "Approved Reports", roles: ["producer", "editor", "super", "programmer"], icon: (
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-file-check"><path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"/><polyline points="14 2 14 8 20 8"/><path d="M9 14l2 2 4-4"/></svg>
    ) },
  ];

  if (loading) {
    return (
      <Navbar expand="lg" className="navbar-custom" aria-busy="true">
        <Container>
          <Navbar.Brand href="#home">
            <img src={logo} alt="PBS NIMS Logo" />
            PBS NIMS
          </Navbar.Brand>
          <div>Loading...</div>
        </Container>
      </Navbar>
    );
  }

  return (
    <>
      <Navbar expand="lg" className="navbar-custom">
        <Container>
          <Navbar.Brand href="#home">
            <img src={logo} alt="PBS NIMS Logo" />
            PBS NIMS
          </Navbar.Brand>
          <Navbar.Toggle
            aria-controls="basic-navbar-nav"
            aria-label="Toggle navigation"
            className="custom-toggle"
          />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              {navLinks.map(({ to, label, roles, icon }) =>
                hasRole(roles) ? (
                  <NavLink
                    key={to}
                    to={to}
                    className={({ isActive }) =>
                      isActive ? "nav-link active" : "nav-link"
                    }
                    aria-current={location.pathname === to ? "page" : undefined}
                  >
                    {icon}
                    {label}
                  </NavLink>
                ) : null
              )}
            </Nav>
            <Nav className="ms-auto" style={{ alignItems: "center" }}>
              <NavLink to="/profile" className="nav-link" style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
                <img src={userAvatar} alt="User Avatar" style={{ width: 24, height: 24, borderRadius: "50%" }} />
                Hello, {user ? user.name.first : "Guest"}
              </NavLink>
              <Nav.Link onClick={handleLogoutClick} className="nav-link" role="button" tabIndex={0}>
                Logout
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <LogoutModal
        show={showLogoutModal}
        handleClose={handleLogoutCancel}
        handleConfirm={handleLogoutConfirm}
      />
    </>
  );
};

export default Navigation;
