import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import moment from "moment";
import { Button } from "react-bootstrap";
import StoryModal from "./StoryModal"; // Import StoryModal component
import ReactPaginate from "react-paginate"; // Import React Paginate
import "../styles/Pagination.css";

const Homepage = () => {
  const [approvedReports, setApprovedReports] = useState([]);
  const [showModal, setShowModal] = useState(false); // Modal visibility state
  const [selectedReport, setSelectedReport] = useState(null); // Store selected report
  const [searchQuery, setSearchQuery] = useState(""); // Search query state
  const [currentPage, setCurrentPage] = useState(0); // Current page (0-based index)
  const rowsPerPage = 20; // Number of rows per page

  // Check if the user is logged in and get the token
  const user = JSON.parse(localStorage.getItem("user") || sessionStorage.getItem("user"));
  const token = user ? user.token : null;

  const config = {
    headers: {
      Authorization: token ? `Bearer ${token}` : "", // Ensure token exists before appending
    },
  };

  useEffect(() => {
    if (token) {
      fetchApprovedReports();
    } else {
      console.error("No token found, user is not authenticated");
      // Optionally, redirect to login or show a message
    }
  }, [token]); // Fetch reports when token is available

  const fetchApprovedReports = () => {
    axios
      .get("https://api.radiopilipinas.online/nims/view", config)
      .then((res) => {
        const filteredApprovedReports = res.data.newsDataList.filter(report => report.approved === true);
        setApprovedReports(filteredApprovedReports);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // Show StoryModal
  const handleShowModal = (report) => {
    setSelectedReport(report); // Set the selected report
    setShowModal(true); // Show the modal
  };

  // Close StoryModal
  const handleCloseModal = () => {
    setShowModal(false);
  };

  // Filter reports based on search query
  const filterReports = () => {
    return approvedReports.filter((report) => {
      const { author, lead, tags, dateCreated } = report;
      const formattedDate = moment(dateCreated).format("MM/DD/YYYY, h:mm:ss a");
      const tagsToSearch = Array.isArray(tags) ? tags.join(', ') : ''; // Join tags into a string if it's an array

      return (
        (author.station && author.station.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (author.name.first && author.name.first.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (author.name.middle && author.name.middle.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (author.name.last && author.name.last.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (lead && lead.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (tagsToSearch.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (formattedDate && formattedDate.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    });
  };

  // Calculate paginated reports for current page
  const paginatedReports = filterReports().slice(
    currentPage * rowsPerPage,
    (currentPage + 1) * rowsPerPage
  );

  // Handle page change
  const handlePageClick = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
  };

  // Delete report function
  const handleDeleteReport = (reportId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this report?");
    if (confirmDelete) {
      axios
        .delete(`https://api.radiopilipinas.online/nims/delete/${reportId}`, config)
        .then(() => {
          // Filter out the deleted report from state
          setApprovedReports(approvedReports.filter(report => report._id !== reportId));
        })
        .catch((err) => {
          console.error(err);
        });
    }
  };

  return (
    <div>
      <Navbar />
      <br />
      <div className="container my-2 border p-4 rounded-lg shadow-lg">
        <div className="card shadow-sm p-4">
          <h3 className="text-xl text-center mb-4">APPROVED REPORTS</h3>
          <hr />

          {/* Search input */}
          <input
            type="text"
            className="form-control mb-4"
            placeholder="Search by source, headline, lead, tags, or date/time"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />

          {paginatedReports.length ? (
            <div className="feed">
              {paginatedReports.map((report) => (
                <div key={report._id} className="card mb-4 shadow-sm">
                  <div className="card-header d-flex justify-content-between align-items-center">
                    <div>
                      <strong>{report.author.name.first} {report.author.name.last}</strong> - <em>{report.author.station}</em>
                    </div>
                    <div className="text-muted" style={{ fontSize: "0.8rem" }}>
                      {moment(report.dateCreated).format("MMMM Do YYYY, h:mm a")}
                    </div>
                  </div>
                  <div className="card-body">
                    {report.image || report.imageUrl ? (
                      <img
                        src={report.image || report.imageUrl}
                        alt="Report"
                        style={{ width: "100%", maxHeight: "300px", objectFit: "cover", marginBottom: "1rem", borderRadius: "5px" }}
                      />
                    ) : null}
                    <h5 className="card-title">{report.title}</h5>
                    <p className="card-text">{report.lead}</p>
                    <p>
                      <strong>Tags: </strong>{report.tags.join(', ')}
                    </p>
                    <p>
                      <strong>Remarks: </strong>{report.remarks}
                    </p>

                    {/* Display approvedBy */}
                    {report.approvedBy && (
                      <p>
                        <strong>Approved By: </strong>{report.approvedBy.name.first} {report.approvedBy.name.last}
                      </p>
                    )}

                    {/* Display images */}
                    {report.files && report.files.images && report.files.images.length > 0 && (
                      <div className="media-section" style={{ marginBottom: "1rem", width: "1092px", height: "910px", overflow: "hidden", position: "relative" }}>
                        <h6 style={{ fontWeight: "600", marginBottom: "0.5rem" }}>Images:</h6>
                        <div style={{ display: "flex", flexWrap: "nowrap", width: "1092px", height: "910px", position: "relative" }}>
                          {/* First column */}
                          <div style={{ width: "430px", height: "910px", position: "relative", overflow: "hidden" }}>
                            {report.files.images[0] && (
                              <img
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[0]}`}
                                alt="news-image-0"
                                style={{
                                  position: "absolute",
                                  top: 0,
                                  left: 0,
                                  width: "430px",
                                  height: "300px",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                  boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                  cursor: "pointer",
                                }}
                                draggable="true"
                                onDragStart={(e) => {
                                  e.dataTransfer.setData('DownloadURL', `image/jpeg:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[0]}`);
                                }}
                              />
                            )}
                            {report.files.images[1] && (
                              <img
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[1]}`}
                                alt="news-image-1"
                                style={{
                                  position: "absolute",
                                  top: "310px",
                                  left: 0,
                                  width: "430px",
                                  height: "300px",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                  boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                  cursor: "pointer",
                                }}
                                draggable="true"
                                onDragStart={(e) => {
                                  e.dataTransfer.setData('DownloadURL', `image/jpeg:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[1]}`);
                                }}
                              />
                            )}
                            {report.files.images[2] && (
                              <img
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[2]}`}
                                alt="news-image-2"
                                style={{
                                  position: "absolute",
                                  top: "620px",
                                  left: 0,
                                  width: "430px",
                                  height: "290px",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                  boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                  cursor: "pointer",
                                }}
                                draggable="true"
                                onDragStart={(e) => {
                                  e.dataTransfer.setData('DownloadURL', `image/jpeg:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[2]}`);
                                }}
                              />
                            )}
                          </div>
                          {/* Second column */}
                          <div style={{ width: "662px", height: "910px", position: "relative", overflow: "hidden", marginLeft: "10px" }}>
                            {report.files.images[3] && (
                              <img
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[3]}`}
                                alt="news-image-3"
                                style={{
                                  position: "absolute",
                                  top: 0,
                                  left: 0,
                                  width: "330px",
                                  height: "450px",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                  boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                  cursor: "pointer",
                                }}
                                draggable="true"
                                onDragStart={(e) => {
                                  e.dataTransfer.setData('DownloadURL', `image/jpeg:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[3]}`);
                                }}
                              />
                            )}
                            {report.files.images[4] && (
                              <img
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[4]}`}
                                alt="news-image-4"
                                style={{
                                  position: "absolute",
                                  top: "460px",
                                  left: 0,
                                  width: "330px",
                                  height: "450px",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                  boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                  cursor: "pointer",
                                }}
                                draggable="true"
                                onDragStart={(e) => {
                                  e.dataTransfer.setData('DownloadURL', `image/jpeg:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${report.files.images[4]}`);
                                }}
                              />
                            )}
                            {/* Additional images can be added similarly */}
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Display audio */}
                    {report.files && report.files.audios && report.files.audios.length > 0 && (
                      <div className="media-section" style={{ marginBottom: "1rem" }}>
                        <h6 style={{ fontWeight: "600", marginBottom: "0.5rem" }}>Audio:</h6>
                        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
                          {report.files.audios.map((audio, index) => (
                            <audio
                              key={index}
                              controls
                              style={{
                                width: "100%",
                                maxWidth: "400px",
                                borderRadius: "8px",
                                boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                cursor: "pointer",
                              }}
                              draggable="true"
                              onDragStart={(e) => {
                                e.dataTransfer.setData('DownloadURL', `audio/mpeg:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${audio}`);
                              }}
                            >
                              <source
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${audio}`}
                                type="audio/mpeg"
                              />
                              Your browser does not support the audio tag.
                            </audio>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Display videos */}
                    {report.files && report.files.videos && report.files.videos.length > 0 && (
                      <div className="media-section" style={{ marginBottom: "1rem" }}>
                        <h6 style={{ fontWeight: "600", marginBottom: "0.5rem" }}>Videos:</h6>
                        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
                          {report.files.videos.map((video, vidIndex) => (
                            <video
                              key={vidIndex}
                              controls
                              style={{
                                width: "100%",
                                maxHeight: "300px",
                                borderRadius: "8px",
                                boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                                cursor: "pointer",
                              }}
                              draggable="true"
                              onDragStart={(e) => {
                                e.dataTransfer.setData('DownloadURL', `video/mp4:https://pbs-nims.s3.ap-southeast-1.amazonaws.com${video}`);
                              }}
                            >
                              <source
                                src={`https://pbs-nims.s3.ap-southeast-1.amazonaws.com${video}`}
                                type="video/mp4"
                              />
                              Your browser does not support the video element.
                            </video>
                          ))}
                        </div>
                      </div>
                    )}

                    <Button variant="primary" onClick={() => handleShowModal(report)}>
                      Read Full Story
                    </Button>{" "}
                    <Button variant="danger" onClick={() => handleDeleteReport(report._id)}>
                      Delete
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center my-4">No approved reports found!</div>
          )}

          <ReactPaginate
            previousLabel={<span>&laquo; Previous</span>}
            nextLabel={<span>Next &raquo;</span>}
            pageCount={Math.ceil(filterReports().length / rowsPerPage)}
            onPageChange={handlePageClick}
            containerClassName={"pagination"}
            pageClassName={"page-item"}
            pageLinkClassName={"page-link"}
            previousClassName={"page-item"}
            nextClassName={"page-item"}
            previousLinkClassName={"page-link"}
            nextLinkClassName={"page-link"}
            activeClassName={"active"}
            disabledClassName={"disabled"}
          />
        </div>
      </div>

      {/* Story Modal */}
      {showModal && selectedReport && (
        <StoryModal
          showModal={showModal}
          handleCloseModal={handleCloseModal}
          selectedNewsItem={selectedReport}
        />
      )}
    </div>
  );
};

export default Homepage;
