import React from "react";

const LoginScreen = () => {
  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white rounded-2xl shadow-lg flex overflow-hidden w-3/4 max-w-4xl">
        {/* Left Side - Form */}
        <div className="w-1/2 p-10">
          <h2 className="text-gray-500 text-sm font-semibold">WELCOME BACK</h2>
          <h1 className="text-3xl font-bold mt-2 mb-4">Login to your account<span className="text-blue-500">.</span></h1>
          <p className="text-gray-400 text-sm">Don't have an account? <span className="text-blue-500 cursor-pointer">Sign Up</span></p>
          
          <form className="mt-6">
            <input type="email" placeholder="Email" className="w-full p-3 border rounded-lg focus:ring focus:ring-blue-300 mb-4" />
            <input type="password" placeholder="Password" className="w-full p-3 border rounded-lg focus:ring focus:ring-blue-300 mb-4" />
            
            <button className="w-full bg-blue-500 text-white p-3 rounded-lg font-semibold hover:bg-blue-600">Login</button>
          </form>
        </div>
        
        {/* Right Side - Image */}
        <div className="w-1/2 relative">
          <img src="/mnt/data/image.png" alt="Background" className="w-full h-full object-cover" />
        </div>
      </div>
    </div>
  );
};

export default LoginScreen;
