import React, { useState, useEffect } from "react";
import "../styles/Login.css";
import profile from "../logo/user2.png";
import user from "../logo/user.png";
import passwordImg from "../logo/pss.jpg";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const LoginForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const navigate = useNavigate();

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle("dark-mode");
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage("");
    setSuccessMessage("");

    const data = JSON.stringify({ username, password });

    try {
      const response = await axios.post(
        `https://api.radiopilipinas.online/login`,
        data,
        { headers: { "Content-Type": "application/json" } }
      );

      localStorage.setItem("user", JSON.stringify(response.data));

      const userDetailsResponse = await axios.get(
        `https://api.radiopilipinas.online/login/getDetails`,
        {
          headers: {
            Authorization: `Bearer ${response.data.token}`,
          },
        }
      );

      localStorage.setItem("userDetails", JSON.stringify(userDetailsResponse.data.userData));
      setSuccessMessage("Login successful!");
      navigate("/homepage");
    } catch (err) {
      setErrorMessage("Incorrect username or password!");
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="main">
      <div className="sub-main">
        <div className="container-img">
          <img src={profile} alt="profile" className="profile" />
        </div>
        <h1>LOGIN</h1>
        <form name="login" onSubmit={handleLogin}>
          <div className="form-group position-relative">
            <img src={user} alt="user" className="user" />
            <input
              type="text"
              id="username"
              name="username"
              className="form-control"
              placeholder="Username"
              onChange={(e) => setUsername(e.target.value)}
              value={username}
              required
            />
          </div>
          <div className="form-group position-relative">
            <img src={passwordImg} alt="password" className="user" />
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              name="password"
              className="form-control"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              required
            />
            <i
              className={`fa ${showPassword ? "fa-eye" : "fa-eye-slash"}`}
              onClick={togglePasswordVisibility}
              style={{ position: "absolute", top: "50%", right: "12px", transform: "translateY(-50%)", cursor: "pointer" }}
            ></i>
          </div>
          <div className="form-group">
            <label>
              <input
                type="checkbox"
                checked={rememberMe}
                onChange={() => setRememberMe(!rememberMe)}
              />
              Remember me
            </label>
          </div>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
          {successMessage && <div className="success-message">{successMessage}</div>}
          <div className="form-group">
            <button type="submit" className="login-btn" disabled={loading}>
              {loading ? "Loading..." : "Login"}
            </button>
          </div>
        </form>
        <div className="dark-mode-toggle">
          <button onClick={toggleDarkMode}>{darkMode ? "Light Mode" : "Dark Mode"}</button>
        </div>
      </div>
    </section>
  );
};

export default LoginForm;
