import React, { useState } from "react";
import moment from "moment";
import { Button } from "react-bootstrap";
import { FaBookOpen, FaTrash } from "react-icons/fa";

const ReportCard = ({ report, handleShowModal, handleDeleteReport, handleShowMediaModal, searchQuery }) => {
  const [expandedReports, setExpandedReports] = useState({});

  // Helper function to highlight matching text
  const highlightText = (text, query) => {
    if (!query) return text;
    const regex = new RegExp(`(${query.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&')})`, "gi");
    const parts = text.split(regex);
    return parts.map((part, i) =>
      regex.test(part) ? (
        <mark key={i} style={{ backgroundColor: "#ffff00", padding: 0 }}>{part}</mark>
      ) : (
        part
      )
    );
  };

  // Toggle expanded state for lead and body
  const toggleExpand = (key) => {
    setExpandedReports((prev) => ({
      ...prev,
      [key]: !prev[key],
    }));
  };

  // Combined media for images and videos
  const combinedMedia = [];

  if (report.image || report.imageUrl) {
    combinedMedia.push({
      type: "image",
      url: report.image || report.imageUrl,
    });
  }

  if (report.files && report.files.images && report.files.images.length > 0) {
    report.files.images.forEach((img) => {
      combinedMedia.push({
        type: "image",
        url: `https://pbs-nims.s3.ap-southeast-1.amazonaws.com${img}`,
      });
    });
  }

  if (report.files && report.files.videos && report.files.videos.length > 0) {
    report.files.videos.forEach((video) => {
      combinedMedia.push({
        type: "video",
        url: `https://pbs-nims.s3.ap-southeast-1.amazonaws.com${video}`,
      });
    });
  }

  return (
    <div className="post card mb-4 shadow-sm">
      <div className="post-header d-flex align-items-center mb-3">
        <div>
          <strong style={{ fontSize: "1.1rem" }}>
            {highlightText(`${report.author.name.first} ${report.author.name.last}`, searchQuery)}
          </strong>
          <div style={{ fontSize: "0.85rem", color: "#123458" }}>
            {highlightText(report.author.station, searchQuery)}
          </div>
          <div style={{ fontSize: "0.75rem", color: "#D4C9BE" }}>
            {moment(report.dateCreated).fromNow()}
          </div>
        </div>
      </div>
      <div className="post-content mb-3">
        <h5 style={{ fontWeight: "600", color: "#123458" }}>{highlightText(report.title, searchQuery)}</h5>
        <p>
          {expandedReports[report._id]
            ? highlightText(report.lead || "", searchQuery)
            : (typeof report.lead === "string"
                ? highlightText(report.lead.split(" ").slice(0, 30).join(" "), searchQuery)
                : "") +
              ((typeof report.lead === "string" && report.lead.split(" ").length > 30) ? "..." : "")}
          {(typeof report.lead === "string" && report.lead.split(" ").length > 30) && (
            <span
              onClick={() => toggleExpand(report._id)}
              style={{ color: "#123458", cursor: "pointer", marginLeft: "5px" }}
            >
              {expandedReports[report._id] ? " see less" : " see more"}
            </span>
          )}
        </p>
        <p style={{ textAlign: "justify", color: "#030303" }}>
          <strong style={{ color: "#123458" }}>Story: </strong>
          {expandedReports[report._id + "_body"]
            ? highlightText(report.body || "", searchQuery)
            : (typeof report.body === "string"
                ? highlightText(report.body.split(" ").slice(0, 50).join(" "), searchQuery)
                : "") +
              ((typeof report.body === "string" && report.body.split(" ").length > 50) ? "..." : "")}
          {(typeof report.body === "string" && report.body.split(" ").length > 50) && (
            <span
              onClick={() => toggleExpand(report._id + "_body")}
              style={{ color: "#123458", cursor: "pointer", marginLeft: "5px" }}
            >
              {expandedReports[report._id + "_body"] ? " see less" : " see more"}
            </span>
          )}
        </p>
        <p>
          <strong style={{ color: "#123458" }}>Tags: </strong>
          {highlightText(report.tags.join(", "), searchQuery)}
        </p>
        <p>
          <strong style={{ color: "#123458" }}>Remarks: </strong>
          {highlightText(report.remarks || "", searchQuery)}
        </p>
      </div>

      {/* Media Section */}
      {combinedMedia.length > 0 && (
        <div
          className="media-section mb-3"
          style={{
            display: combinedMedia.length >= 3 ? "grid" : "flex",
            gridTemplateColumns: combinedMedia.length >= 3 ? "repeat(2, 600px)" : undefined,
            gridTemplateRows: combinedMedia.length >= 3 ? "repeat(2, 400px)" : undefined,
            gap: "10px",
            overflowX: combinedMedia.length >= 3 ? "hidden" : "auto",
            flexWrap: combinedMedia.length >= 3 ? "nowrap" : "wrap",
            justifyContent: "center",
            maxWidth: combinedMedia.length >= 3 ? "1220px" : "100%",
            height: combinedMedia.length >= 3 ? "820px" : "auto",
          }}
        >
          {combinedMedia.length >= 4
            ? combinedMedia.slice(0, 4).map((media, idx) => {
                const commonStyle = {
                  width: "100%",
                  height: "100%",
                  borderRadius: "10px",
                  cursor: "pointer",
                  objectFit: "cover",
                  boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                };
                if (media.type === "image") {
                  if (idx === 3 && combinedMedia.length > 4) {
                    return (
                      <div
                        key={idx}
                        style={{
                          position: "relative",
                          width: "100%",
                          height: "100%",
                          borderRadius: "10px",
                          cursor: "pointer",
                          overflow: "hidden",
                          boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                        }}
                        onClick={() => handleShowMediaModal(combinedMedia, null, idx)}
                      >
                        <img
                          src={media.url}
                          alt={`media-${idx}`}
                          style={{
                            width: "100%",
                            height: "100%",
                            objectFit: "cover",
                            filter: "brightness(70%)",
                          }}
                          draggable="true"
                          onDragStart={(e) => {
                            e.dataTransfer.setData("DownloadURL", `image/jpeg:${media.url}`);
                          }}
                        />
                        <div
                          style={{
                            position: "absolute",
                            top: "50%",
                            left: "50%",
                            transform: "translate(-50%, -50%)",
                            color: "white",
                            fontSize: "2.5rem",
                            fontWeight: "bold",
                            userSelect: "none",
                            pointerEvents: "none",
                          }}
                        >
                          +{combinedMedia.length - 4}
                        </div>
                      </div>
                    );
                  }
                  return (
                    <img
                      key={idx}
                      src={media.url}
                      alt={`media-${idx}`}
                      style={commonStyle}
                      draggable="true"
                      onDragStart={(e) => {
                        e.dataTransfer.setData("DownloadURL", `image/jpeg:${media.url}`);
                      }}
                      onClick={() => handleShowMediaModal(combinedMedia, null, idx)}
                    />
                  );
                } else if (media.type === "video") {
                  if (idx === 3 && combinedMedia.length > 4) {
                    return (
                      <div
                        key={idx}
                        style={{
                          position: "relative",
                          width: "80%",
                          height: "100%",
                          borderRadius: "10px",
                          cursor: "pointer",
                          overflow: "hidden",
                          boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                        }}
                        onClick={() => handleShowMediaModal(combinedMedia, null, idx)}
                      >
                        <video
                          controls
                          style={{
                            width: "80%",
                            height: "100%",
                            objectFit: "cover",
                            filter: "brightness(70%)",
                            borderRadius: "10px",
                          }}
                          draggable="true"
                          onDragStart={(e) => {
                            e.dataTransfer.setData("DownloadURL", `video/mp4:${media.url}`);
                          }}
                        >
                          <source src={media.url} type="video/mp4" />
                          Your browser does not support the video element.
                        </video>
                        <div
                          style={{
                            position: "absolute",
                            top: "50%",
                            left: "50%",
                            transform: "translate(-50%, -50%)",
                            color: "white",
                            fontSize: "2.5rem",
                            fontWeight: "bold",
                            userSelect: "none",
                            pointerEvents: "none",
                          }}
                        >
                          +{combinedMedia.length - 4}
                        </div>
                      </div>
                    );
                  }
                  return (
                    <video
                      key={idx}
                      controls
                      style={commonStyle}
                      draggable="true"
                      onDragStart={(e) => {
                        e.dataTransfer.setData("DownloadURL", `video/mp4:${media.url}`);
                      }}
                      onClick={() => handleShowMediaModal(combinedMedia, null, idx)}
                    >
                      <source src={media.url} type="video/mp4" />
                      Your browser does not support the video element.
                    </video>
                  );
                } else {
                  return null;
                }
              })
            : combinedMedia.map((media, idx) => {
                if (media.type === "image") {
                  return (
                    <img
                      key={idx}
                      src={media.url}
                      alt={`media-${idx}`}
                    style={{
                      width: "520px",
                      height: "400px",
                      borderRadius: "10px",
                      cursor: "pointer",
                      flexShrink: 0,
                      objectFit: "contain",
                    }}
                      draggable="true"
                      onDragStart={(e) => {
                        e.dataTransfer.setData("DownloadURL", `image/jpeg:${media.url}`);
                      }}
                      onClick={() => handleShowMediaModal(combinedMedia, null, idx)}
                    />
                  );
                } else if (media.type === "video") {
                  return (
                    <video
                      key={idx}
                      controls
                      style={{
                        width: "520px",
                        height: "400px",
                        maxWidth: "600px",
                        objectFit: "contain",
                        borderRadius: "10px",
                        boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                        cursor: "pointer",
                        flexShrink: 0,
                      }}
                      draggable="true"
                      onDragStart={(e) => {
                        e.dataTransfer.setData("DownloadURL", `video/mp4:${media.url}`);
                      }}
                      onClick={() => handleShowMediaModal(combinedMedia, null, idx)}
                    >
                      <source src={media.url} type="video/mp4" />
                      Your browser does not support the video element.
                    </video>
                  );
                } else {
                  return null;
                }
              })}
        </div>
      )}

      {/* Separate Audio Section */}
      {report.files && report.files.audios && report.files.audios.length > 0 && (
        <div className="media-section mb-3">
          <h6 style={{ fontWeight: "600", marginBottom: "0.5rem" }}>Audio:</h6>
          <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            {report.files.audios.map((audio, index) => {
              const audioUrl = `https://pbs-nims.s3.ap-southeast-1.amazonaws.com${audio}`;
              return (
                <audio
                  key={index}
                  controls
                  style={{
                    width: "100%",
                    maxWidth: "400px",
                    borderRadius: "8px",
                    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                    cursor: "pointer",
                  }}
                  draggable="true"
                  onDragStart={(e) => {
                    e.dataTransfer.setData("DownloadURL", `audio/mpeg:${audioUrl}`);
                  }}
                  onClick={() =>
                    handleShowMediaModal(
                      report.files.audios.map((a) => ({
                        type: "audio",
                        url: `https://pbs-nims.s3.ap-southeast-1.amazonaws.com${a}`,
                      })),
                      "audio",
                      index
                    )
                  }
                >
                  <source src={audioUrl} type="audio/mpeg" />
                  Your browser does not support the audio tag.
                </audio>
              );
            })}
          </div>
        </div>
      )}

      <div className="post-actions d-flex justify-content-between mt-3">
        <Button
          variant="primary"
          size="sm"
          onClick={() => handleShowModal(report)}
          aria-label={`Read full story of ${report.title}`}
        >
          <FaBookOpen style={{ marginRight: "6px" }} />
          Read Full Story
        </Button>
        <Button
          variant="danger"
          size="sm"
          onClick={() => handleDeleteReport(report._id)}
          aria-label={`Delete report titled ${report.title}`}
        >
          <FaTrash style={{ marginRight: "6px" }} />
          Delete
        </Button>
      </div>
    </div>
  );
};

export default ReportCard;
